mapper.update:
UPDATE orders
SET is_delete = 1
WHERE id = #{orderId} AND user_id = #{userId}

UPDATE orders
SET is_comment = 1
WHERE id = #{orderId} AND user_id = #{userId}

mapper.select:
SELECT o1.id as orderId, o3.order_status as orderStatus, o1.user_id as userId, o1.real_pay_amount as realPayAmount, o1.pay_method as payMethod, o1.is_comment as isComment, o1.created_time as createdTime
FROM orders o1 LEFT JOIN order_status o3 on o1.id = o3.order_id WHERE o1.user_id = #{queryParams.userId} AND o1.is_delete != 1
and o3.order_status = #{queryParams.orderStatus}
LIMIT #{queryParams.start}, #{queryParams.end}

SELECT
    COUNT(1)
FROM
    orders o
LEFT JOIN order_status os
ON o.id = os.order_id
WHERE o.user_id = #{queryParams.userId}
AND os.order_status = #{queryParams.orderStatus}

    AND o.is_comment = #{queryParams.isComment}
